import React, { useEffect, useState } from "react";

import classes from "./Navbar.module.scss";
import { Link, useNavigate } from "react-router-dom";
import mainlogo from '../components/img/mainlogo.png';

const Navbar = () => {
    const navigate = useNavigate();
    const [menuOpen, setMenuOpen] = useState(false);
    const [size, setSize] = useState({
        width: undefined,
        height: undefined,
    });
    const handleClick = () => setMenuOpen(!menuOpen);

    useEffect(() => {
        const handleResize = () => {
            setSize({
                width: window.innerWidth,
                height: window.innerHeight,
            });
        };
        window.addEventListener("resize", handleResize);

        return () => window.removeEventListener("resize", handleResize);
    }, []);

    useEffect(() => {
        if (size.width > 890 && menuOpen) {
            setMenuOpen(false);
        }
    }, [size.width, menuOpen]);

    const menuToggleHandler = () => {
        setMenuOpen((p) => !p);
        
    };

    const ctaClickHandler = () => {
        menuToggleHandler();
        navigate.push("/SignUp");
    };

    return (
        <header className={classes.header}>
            <div className={classes.header__content}>
                <Link to="/1" className={classes.header__content__logo}>
                    <img className={classes.header__content__logo} src={mainlogo}/>
                </Link>
                <nav
                    className={`${classes.header__content__nav} ${
                        menuOpen && size.width < 890 ? classes.isMenu : ""
                    }`}
                >
                    <ul>
                        <li>
                            <Link to="/main" onClick={menuToggleHandler}>
                                AI
                            </Link>
                        </li>
                        <li>
                            <Link to="/dnd" onClick={menuToggleHandler}>
                                자유게시판
                            </Link>
                        </li>
                        <li>
                            <Link to="/RestauR" onClick={menuToggleHandler}>
                                마켓
                            </Link>
                        </li>
                        <li>
                            <Link to="/Inform" onClick={menuToggleHandler}>
                                회원정보
                            </Link>
                        </li>
                    </ul>
                    <button onClick={ctaClickHandler}>Sign up</button>
                </nav>
                <div className={classes.header__content__toggle} onClick={handleClick} >
                    <input className={classes.header__content.button} ></input>
                    {!menuOpen ? (
                        <i className='fas fa-bars' onClick={menuToggleHandler} />
                    ) : (
                        <i className='fas fa-times' onClick={menuToggleHandler} />
                    )}
                </div>
            </div>
        </header>
    );
};

export default Navbar;
