import React, { useEffect, useState } from "react";

import classes from "./Navbar.module.scss";
import { Link, useNavigate } from "react-router-dom";
import mainlogo from '../components/img/mainlogo.png';
import styled from "styled-components";

const MenuLabel = styled.label`
  background-color: #b6edc8;
  position: fixed;
  top: 3%;
  right: 3%;
  border-radius: 50%;
  height: 3rem;
  width: 3rem;
  cursor: pointer;
  z-index: 1000;
  box-shadow: 0 1rem 3rem rgba(182, 237, 200, 0.3);
  text-align: center;
`;

const Icon = styled.span`
  position: fixed;
  background-color: ${(props) => (props.clicked ? "transparent" : "black")};
  top : 3.5%;
  right : 4.2%;
  width: 2rem;
  height: 2px;
  display: inline-block;
  margin-top: 1.2rem;
  transition: all 0.3s;
  &::before,
  &::after {
    content: "";
    background-color: black;
    width: 2rem;
    height: 2px;
    display: inline-block;
    position: absolute;
    left: 0;
    transition: all 0.3s;
  }
  &::before {
    top: ${(props) => (props.clicked ? "0" : "-0.8rem")};
    transform: ${(props) => (props.clicked ? "rotate(135deg)" : "rotate(0)")};
  }
  &::after {
    top: ${(props) => (props.clicked ? "0" : "0.8rem")};
    transform: ${(props) => (props.clicked ? "rotate(-135deg)" : "rotate(0)")};
  }
  ${MenuLabel}:hover &::before {
    top: ${(props) => (props.clicked ? "0" : "-1rem")};
  }
  ${MenuLabel}:hover &::after {
    top: ${(props) => (props.clicked ? "0" : "1rem")};
  }
`;


 


const Navbar = () => {
    const navigate = useNavigate();
    const [menuOpen, setMenuOpen] = useState(false);
    const [size, setSize] = useState({
        width: undefined,
        height: undefined,
    });
    const handleClick = () => setMenuOpen(!menuOpen);

    useEffect(() => {
        const handleResize = () => {
            setSize({
                width: window.innerWidth,
                height: window.innerHeight,
            });
        };
        window.addEventListener("resize", handleResize);

        return () => window.removeEventListener("resize", handleResize);
    }, []);

    useEffect(() => {
        if (size.width > 890 && menuOpen) {
            setMenuOpen(false);
        }
    }, [size.width, menuOpen]);

    const menuToggleHandler = () => {
        setMenuOpen((p) => !p);
        
    };

    const ctaClickHandler = () => {
        menuToggleHandler();
        navigate.push("/SignUp");
    };

    return (
        <header className={classes.header}>
            <div className={classes.header__content}>
                <Link to="/1" className={classes.header__content__logo}>
                    <img className={classes.header__content__logo} src={mainlogo}/>
                </Link>
                <nav
                    className={`${classes.header__content__nav} ${
                        menuOpen && size.width < 890 ? classes.isMenu : ""
                    }`}
                >
                    <ul>
                        <li>
                            <Link to="/main" onClick={menuToggleHandler}>
                                AI
                            </Link>
                        </li>
                        <li>
                            <Link to="/dnd" onClick={menuToggleHandler}>
                                자유게시판
                            </Link>
                        </li>
                        <li>
                            <Link to="/RestauR" onClick={menuToggleHandler}>
                                마켓
                            </Link>
                        </li>
                        <li>
                            <Link to="/Inform" onClick={menuToggleHandler}>
                                회원정보
                            </Link>
                        </li>
                    </ul>
                    <button onClick={ctaClickHandler}>Sign up</button>
                </nav>
                <div className={classes.header__content__toggle} onClick={handleClick} >
                        <MenuLabel htmlFor="navi-toggle" onClick={handleClick}>
                            <Icon menuOpen={setMenuOpen}></Icon>
                        </MenuLabel>
                    {!menuOpen ? (
                        <i className='fas fa-bars' onClick={menuToggleHandler} />
                    ) : (
                        <i className='fas fa-times' onClick={menuToggleHandler} />
                    )}
                </div>
            </div>
        </header>
    );
};

export default Navbar;
