import React from "react";
import "antd/dist/antd.min.css";
import { Carousel } from "antd";
import carrot from '../components/img/carrot.jpeg'


const contentStyle = {
  height: '400px',
  width: '100%',
  color: '#fff',
  lineHeight: '160px',
  textAlign: 'center',
  background: '#364d79',
};

export default function Main() { 
  
  return(
    <Carousel autoplay>
      <div>
        <img style={contentStyle} src={carrot}></img>
      </div>
      <div>
        <h3 style={contentStyle}>2</h3>
      </div>
      <div>
        <h3 style={contentStyle}>3</h3>
      </div>
      <div>
        <h3 style={contentStyle}>4</h3>
      </div>
    </Carousel>
  );
}
